          max: 100,
          axisLabel: {
            fontSize: 14,
                 distance: 10
          },
          detail: {
            formatter: "{value}",
            // backgroundColor: "rgba(227, 0, 0, 1)",
            offsetCenter: ["0", "78%"],
          },
          title: {
            offsetCenter: ["0", "100%"],
            color: "rgba(0, 0, 0, 1)",
          },
          splitNumber: 4,
          axisLine: {
            lineStyle: {
              color: [
                [0.29, "rgb(15,164,100)"],
