              //   [1, "rgb(219,58,55)"],
              // ],
              shadowColor: "rgba(255, 255, 255, 0.7)",
              shadowBlur: 10,
              width: 20,
            },
          },
                     splitLine: {
      show: true,
      length: "20%"}
          // startAngle: 190,
          // endAngle: -0,
        },
        
      ],
    };
  },[])
  
  // render() {
    return (
      <View style={styles.chartContainer}>
        <ECharts
          option={option}
          backgroundColor="rgba(93, 169, 81, 0.3)"
        />
        <Text>Hello</Text>
      </View>
    );
  // }
}

const styles = StyleSheet.create({
  chartContainer: {
    flex: 1,
    width:'100%'
  }
});

export default Chart
