  var option = {
    tooltip: {
      formatter: "{a} <br/>{b} : {c} ppm",
    },
    radius: "100%",
    // toolbox: {
    //   feature: {
    //     restore: {},
    //     saveAsImage: {},
    //   },
    // },
    series: [
      {
        // name: "Ammonia",
        type: "gauge",
        detail: { formatter: "{value} ppm" },
        data: [{ value: {DD}, name: 'this.unit' }],
        max: 100,
        axisLabel: {
          fontSize: 14,
               distance: 10
        },
        detail: {
          formatter: "{value}",
          // backgroundColor: "rgba(227, 0, 0, 1)",
          offsetCenter: ["0", "78%"],
        },
        title: {
          offsetCenter: ["0", "100%"],
          color: "rgba(0, 0, 0, 1)",
        },
        splitNumber: 4,
        axisLine: {
          lineStyle: {
            color: [
              [0.29, "rgb(15,164,100)"],
              [0.7, "rgb(235,137,52)"],
              [1, "rgb(219,58,55)"],
            ],
            // color: [
            //   [0.29, "rgb(115,222,133)"],
            //   [0.7, "rgb(235,137,52)"],
            //   [1, "rgb(219,58,55)"],
            // ],
            shadowColor: "rgba(255, 255, 255, 0.7)",
            shadowBlur: 10,
            width: 20,
          },
        },
                   splitLine: {
    show: true,
    length: "20%"}
        // startAngle: 190,
        // endAngle: -0,
      },
      
    ],
  };
  // render() {
    return (
      <View style={styles.chartContainer}>
        <ECharts
          option={option}
          backgroundColor="rgba(93, 169, 81, 0.3)"
        />
        <Text>Hello</Text>
      </View>
    );
  // }
}

const styles = StyleSheet.create({
  chartContainer: {
    flex: 1,
    width:'100%'
  }
});

export default Chart
