import React, { useState, useEffect } from 'react'
import { StatusBar } from 'expo-status-bar';
import { View, Text, Button, StyleSheet} from 'react-native'
import { useIsFocused } from '@react-navigation/native';
import AsyncStorage from '@react-native-community/async-storage'

var mqtt = require('@taoqf/react-native-mqtt')
var client  = mqtt.connect('wss://tron.airmode.live:8083/mqtt')
import {useSelector} from 'react-redux'
import store from '../store/index'


import Chart from '../component/Chart2'

const CurrentScreen = props =>{
 
  const DD = useSelector(state => state.sensorData.DD)
  const DG = useSelector(state => state.sensorData.DG)
  const DO = useSelector(state => state.sensorData.DO)
  const PH = useSelector(state => state.sensorData.PH)
  const TMP = useSelector(state => state.sensorData.TMP)

  return(
    <View style={styles.container}>
      <Text>SENSOR DATA</Text>
      <View style={styles.dataContainer}>
        <View style={styles.data}>
          <Text>Salinity</Text>
          <Text>{DD} ppt</Text>
          
        </View>
        <View style={styles.data}>
          <Text>Ammonia</Text>
          <Text>{DG} mg/l</Text>
        </View >
        <View style={styles.data}>
          <Text>Dissolve Oxygen</Text>
          <Text>{DO} mg/l</Text>
        </View>
        <View style={styles.data}>
          <Text>pH</Text>
          <Text>{PH} unit</Text>
        </View>
        <View style={styles.data}>
          <Text>Temperature</Text>
          <Text>{TMP} °C</Text>
        </View>
      </View>
      <Button title="Current" onPress={()=>{props.navigation.navigate('Trends')}}/>
      <Chart data={DD}/>
      <StatusBar style="auto" />
    </View>
  )
}


const styles = StyleSheet.create({
  container:{
    flex:1,
    justifyContent:'center',
    alignItems:'center'
  },
  dataContainer:{
    flexDirection:'row',
    flexWrap:'wrap',
    justifyContent:'center',
    alignContent:'center',
    
  },
  data:{
    borderRadius:10,
    borderColor:'black',
    borderWidth:1,
    margin:20,
    padding:20,
    alignItems:'center',
    minWidth:140
  }
})

export default CurrentScreen
